SELECT ar.Name, al.Title, t.Name, g.Name, m.Name, t.UnitPrice FROM Track t INNER JOIN Genre g ON t.GenreId = g.GenreId INNER JOIN MediaType m ON t.MediaTypeId = m.MediaTypeId INNER JOIN Album al ON t.AlbumId =al.AlbumId INNER JOIN Artist ar ON al.ArtistId = ar.ArtistId WHERE t.TrackId <> (SELECT t.TrackId FROM Track t INNER JOIN InvoiceLine l ON t.TrackId=l.TrackId) AND (al.Title ='Vinicius De Moraes' OR al.Title='Lost, Season 1') GROUP BY ar.Name ORDER BY ar.Name ASC;
